var Promise, aMethods, fs, pify;

Promise = require("pinkie-promise");

pify = require("pify");

fs = require("fs");

aMethods = ["access", "readFile", "writeFile", "close", "open", "read", "write", "rename", "rmdir", "mkdir", "readdir", "stat", "lstat", "fstat", "appendFile", "realpath", "link", "unlink", "readlink", "chmod", "fchmod", "chown", "fchown", "lchown", "fsync", "utimes", "futimes", "ftruncate"];

aMethods.forEach(function(sMethod) {
  if (!fs[sMethod]) {
    return;
  }
  return exports[sMethod] = pify(fs[sMethod], Promise);
});
